CREATE TABLE `addressbook`.`contacts` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `first_name` VARCHAR(100) NOT NULL , `last_name` TEXT NOT NULL , `email` VARCHAR(100) NOT NULL , `phone` VARCHAR(100) NOT NULL , `address1` VARCHAR(100) NOT NULL , `address2` VARCHAR(100) NOT NULL , `city` VARCHAR(100) NOT NULL , `states` VARCHAR(100) NOT NULL , `postal_code` VARCHAR(30) NOT NULL , `notes` TEXT NOT NULL , `date_added` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `contact_group` VARCHAR(100) NOT NULL , `username` VARCHAR(100) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB; 
